package struktura_Tipet_Operatoret;

public class SpecialCharacters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("this is a text");
System.out.println("this is a te\txt");//t is for tab
System.out.println("this is a \ntext");//t is for a new line
System.out.println("this is a \'text\'");//t is for single quotes
System.out.println("this is a \"text\"");//t is for double quotes
System.out.println("this is a \\text\\");//t is \
	}

}
